import { useState, useEffect } from "react";
import ReactMapGl, { Marker, Popup } from "react-map-gl";
import { Room, Star } from "@material-ui/icons";
import axios from "axios";
import "./app.css";
function App() {
  const [pins, setPins] = useState([]);
  const [viewport, setViewport] = useState({
    width: "100vw",
    height: "100vh",
    latitude: 40.71506434201678,
    longitude: -73.99684811789366,
    zoom: 10,
  });

  useEffect(() => {
    const getPins = async () => {
      try {
        const response = await axios.get("/pins");
        setPins(response.data);
      } catch (err) {
        console.log(err);
      }
    };
    getPins();
  }, []);

  return (
    <div className="App">
      <ReactMapGl
        {...viewport}
        mapboxApiAccessToken={process.env.REACT_APP_MAPBOX}
        onViewportChange={(nextViewport) => setViewport(nextViewport)}
        mapStyle="mapbox://styles/shansiddiqui94/ckymlk1in22xu14pewxn6niqm"
      >
        {pins.map((p) => (
          <>
            <Marker
              latitude={40.7607183218919}
              longitude={-73.8311948975521}
              offsetLeft={-20}
              offsetTop={-10}
            >
              <Room
                style={{ fontSize: viewport.zoom * 7, color: "slateblue" }}
              ></Room>
            </Marker>
            <Popup
              latitude={40.7607183218919}
              longitude={-73.8311948975521}
              closeButton={true}
              closeOnClick={false}
              anchor="left"
            >
              <div className="card">
                <label>Place</label>
                <h4 className="place">Nice Spot!</h4>
                <label>Review</label>
                <p className="desc">
                  Enjoy the many Asian eateries. I like it!!!
                </p>
                <label>Rating</label>
                <div className="stars">
                  <Star className="star" />
                  <Star className="star" />
                  <Star className="star" />
                  <Star className="star" />
                  <Star className="star" />
                </div>
                <label>Info </label>
                <span className="username">
                  Created by <b>Dan</b>
                </span>
                <span className="date">1 hour ago</span>
              </div>
            </Popup>{" "}
          </>
        ))}
        }
      </ReactMapGl>
    </div>
  );
}

export default App;
