import "./app.css";
import ReactMapGL, { Marker, Popup } from "react-map-gl";
import { useState, useEffect } from "react";

import { Room } from "@material-ui/icons";
import toiletMarker from "/Users/shansiddiqui/Desktop/weeWee/client/src/icons/toilet.svg";
import paperRoll from "/Users/shansiddiqui/Desktop/weeWee/client/src/icons/toilet-paper.svg";
import axios from "axios";
import { format } from "timeago.js";

function App() {
  const [pins, setPins] = useState([]);
  const [currentPlaceId, setCurrentPlaceId] = useState(null);
  const [viewport, setViewport] = useState({
    width: "100vw", //1990
    height: "100vh", // 800
    latitude: 40.73061,
    longitude: -73.935242,
    zoom: 10,
  });

  useEffect(() => {
    const getPins = async () => {
      try {
        const response = await axios.get("/pins");
        setPins(response.data);
      } catch (err) {
        console.log(err);
      }
    };
    getPins();
  }, []);

  const handleMarkerClick = (id) => {
    setCurrentPlaceId(id);
  };

  return (
    <div className="App">
      {" "}
      <ReactMapGL
        {...viewport}
        mapboxApiAccessToken={process.env.REACT_APP_MAPBOX}
        onViewportChange={(nextViewport) => setViewport(nextViewport)}
        mapStyle="mapbox://styles/shansiddiqui94/ckymlk1in22xu14pewxn6niqm"
      >
        {pins.map((p) => {
          <>
            <Marker
              latitude={p.lat}
              longitude={p.long}
              offsetLeft={-20}
              offseetTop={-10}
            >
              <Room
                style={{
                  fontSize: viewport.zoom * 7,
                  color: "grey",
                  cursor: "pointer",
                }}
                onClick={() => handleMarkerClick(p._id)}
              />
              <div className="toilet">
                <img src={toiletMarker} height={35} width={35} alt="LOO" />
              </div>
            </Marker>
            {p._id === currentPlaceId && (
              <Popup
                latitude={p.lat}
                longitude={p.long}
                closeButton={true}
                closeOnClick={false}
                onClose={() => setCurrentPlaceId(null)}
                anchor="left"
              >
                <div className="card">
                  <label>Place:</label>
                  <h4 className="place">{p.title}</h4>
                  <label>Review:</label>
                  <p className="desc">{p.desc}</p>
                  <label>Rating:</label>

                  <div className="stars">
                    <img
                      className="star"
                      src={paperRoll}
                      height={25}
                      width={25}
                      alt="roll"
                    />
                    <img
                      className="star"
                      src={paperRoll}
                      height={25}
                      width={25}
                      alt="roll"
                    />
                    <img
                      className="star"
                      src={paperRoll}
                      height={25}
                      width={25}
                      alt="roll"
                    />
                    <img
                      className="star"
                      src={paperRoll}
                      height={25}
                      width={25}
                      alt="roll"
                    />
                    <img
                      className="star"
                      src={paperRoll}
                      height={25}
                      width={25}
                      alt="roll"
                    />
                  </div>

                  <label>Info </label>
                  <span className="username">
                    Created by <b> {p.username} </b>
                  </span>
                  <span className="date">{format(p.createdAt)}</span>
                </div>
              </Popup>
            )}
          </>;
        })}
      </ReactMapGL>
    </div>
  );
}

export default App;
