import { useState, useEffect } from "react";
import ReactMapGL, { Marker, Popup } from "react-map-gl";
import toiletMarker from "/Users/shansiddiqui/Desktop/weeWee/client/src/icons/toilet.svg";
import paperRoll from "/Users/shansiddiqui/Desktop/weeWee/client/src/icons/toilet-paper.svg";
import "./app.css";
import axios from "axios";
function App() {
  const [pins, setPins] = useState([]);
  const [viewport, setViewport] = useState({
    width: "100vw", //1990
    height: "100vh", // 800
    latitude: 40.73061,
    longitude: -73.935242,
    zoom: 10,
  });

  useEffect(() => {
    const getPins = async () => {
      try {
        const response = await axios.get("/pins");
        setPins(response.data);
      } catch (err) {
        console.log(err);
      }
    };
    getPins();
  }, []);

  return (
    <div className="App">
      {" "}
      <ReactMapGL
        {...viewport}
        mapboxApiAccessToken={process.env.REACT_APP_MAPBOX}
        onViewportChange={(nextViewport) => setViewport(nextViewport)}
        mapStyle="mapbox://styles/shansiddiqui94/ckymlk1in22xu14pewxn6niqm"
      >
        {pins.map((p) => {
          <>
            <Marker
              latitude={p.lat}
              longitude={p.long}
              offsetLeft={-20}
              offseetTop={-10}
            >
              <div>
                <img src={toiletMarker} height={35} width={35} alt="LOO" />
              </div>
            </Marker>
            <Popup
              latitude={40.76902003074009}
              longitude={-73.79200983984981}
              closeButton={true}
              closeOnClick={false}
              // onClose={()=> togglePopup(false)}
              anchor="left"
            >
              <div className="card">
                <label>Place:</label>
                <h4 className="place">Starbucks Bathroom</h4>
                <label>Review:</label>
                <p className="desc">Clean and hygenic restroom, I like it</p>
                <label>Rating:</label>
                <div className="stars">
                  <img
                    className="star"
                    src={paperRoll}
                    height={25}
                    width={25}
                    alt="roll"
                  />
                  <img
                    className="star"
                    src={paperRoll}
                    height={25}
                    width={25}
                    alt="roll"
                  />
                  <img
                    className="star"
                    src={paperRoll}
                    height={25}
                    width={25}
                    alt="roll"
                  />
                  <img
                    className="star"
                    src={paperRoll}
                    height={25}
                    width={25}
                    alt="roll"
                  />
                  <img
                    className="star"
                    src={paperRoll}
                    height={25}
                    width={25}
                    alt="roll"
                  />
                </div>
                <label>Info </label>
                <span className="username">
                  Created by <b> Shan </b>
                </span>

                <span className="date"> 1 hour ago </span>
              </div>
            </Popup>{" "}
          </>;
        })}
      </ReactMapGL>
    </div>
  );
}

export default App;
